import React, { useState, useEffect } from 'react';
import { TextField, Button, Container, Typography, MenuItem, Select, InputLabel, FormControl } from '@mui/material';
import axios from 'axios';

const AddProduct = () => {
  const [formData, setFormData] = useState({
    name: '',
    price: '',
    description: '',
    category: '',
    stock: '',
  });
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/api/categories')
      .then(response => {
        setCategories(response.data);
      })
      .catch(error => {
        console.error('There was an error fetching the categories!', error);
      });
  }, []);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios.post('http://localhost:5000/api/products', formData)
      .then(response => {
        console.log(response.data);
      })
      .catch(error => {
        console.error('There was an error adding the product!', error);
      });
  };

  return (
    <Container>
      <Typography variant="h4">Add Product</Typography>
      <form onSubmit={handleSubmit}>
        <TextField label="Name" name="name" onChange={handleChange} fullWidth margin="normal" />
        <TextField label="Price" name="price" type="number" onChange={handleChange} fullWidth margin="normal" />
        <TextField label="Description" name="description" onChange={handleChange} fullWidth margin="normal" />
        <FormControl fullWidth margin="normal">
          <InputLabel>Category</InputLabel>
          <Select name="category" value={formData.category} onChange={handleChange}>
            {categories.map((category) => (
              <MenuItem key={category._id} value={category._id}>{category.name}</MenuItem>
            ))}
          </Select>
        </FormControl>
        <TextField label="Stock" name="stock" type="number" onChange={handleChange} fullWidth margin="normal" />
        <Button type="submit" variant="contained" color="primary">Add Product</Button>
      </form>
    </Container>
  );
};

export default AddProduct;
